<?php\n// Your friends' user IDs in CSV format\n$friends_csv = "3,4,5,6,7";\n\n// Convert the CSV to an array\n$friends_array = explode(",", $friends_csv);\n\n// Ensure the array is not empty before proceeding\nif (!empty($friends_array)) {\n    // Convert array to a string format suitable for SQL IN clause\n    $friends_list = implode(",", array_map('intval', $friends_array));\n\n    // Create the query to fetch transactions involving any of the friends\n    $query = "SELECT * FROM transaction WHERE user1 IN ($friends_list) OR user2 IN ($friends_list)";\n\n    // Execute the query\n    // Assuming you have a PDO connection named $pdo\n    $stmt = $pdo->prepare($query);\n    $stmt->execute();\n\n    // Fetch all the transactions\n    $transactions = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Display the transactions\n    foreach ($transactions as $transaction) {\n        echo "Transaction ID: " . $transaction['tid'] . ", User1: " . $transaction['user1'] . ", User2: " . $transaction['user2'] . ", Type of Trade: " . $transaction['type_of_trade'] . "<br>";\n    }\n} else {\n    echo "No friends found.";\n}\n?>