<?php\nfunction getArticle($mysqli, $article_id, $version = null) {\n    if ($version === null) {\n        // Fetch the current version\n        $stmt = $mysqli->prepare("SELECT * FROM articles WHERE article_id = ? AND is_current = 1");\n    } else {\n        // Fetch a specific version\n        $stmt = $mysqli->prepare("SELECT * FROM articles WHERE article_id = ? AND version = ?");\n        $stmt->bind_param("ii", $article_id, $version);\n    }\n    $stmt->bind_param("i", $article_id);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    return $result->fetch_assoc();\n}\n?>