<?php\nfunction trimImage($imagePath, $newHeight = 800) {\n    // Load the image\n    $sourceImage = imagecreatefrompng($imagePath);\n    \n    if ($sourceImage === false) {\n        die("Failed to load image.");\n    }\n\n    // Get image dimensions\n    list($width, $height) = getimagesize($imagePath);\n\n    // Create a new true color image with the desired height\n    $trimmedImage = imagecreatetruecolor($width, $newHeight);\n\n    // Copy the top part of the source image to the new image\n    imagecopy($trimmedImage, $sourceImage, 0, 0, 0, 0, $width, $newHeight);\n\n    // Output the image directly to the browser\n    header('Content-Type: image/png');\n    imagepng($trimmedImage);\n\n    // Clean up\n    imagedestroy($sourceImage);\n    imagedestroy($trimmedImage);\n}\n\n// Usage example\n$imagePath = 'path/to/your/image.png';\ntrimImage($imagePath);\n?>