<?php\nsession_start();\n\n// Initialize cart if not set\nif (!isset($_SESSION['cart'])) {\n    $_SESSION['cart'] = array();\n}\n\n// Add item to cart\nif (isset($_GET['add'])) {\n    $item = $_GET['add'];\n    if (!in_array($item, $_SESSION['cart'])) {\n        $_SESSION['cart'][] = $item;\n    }\n}\n\n// View cart\nif (isset($_GET['view']) && $_GET['view'] == 'cart') {\n    // Load XML structure\n    $xml = new SimpleXMLElement('<cart/>');\n    foreach ($_SESSION['cart'] as $item) {\n        $xmlItem = $xml->addChild('item', $item);\n    }\n\n    // Load XSLT\n    $xsl = new DOMDocument();\n    $xsl->load('cart.xsl');\n\n    // Configure the transformer\n    $proc = new XSLTProcessor();\n    $proc->importStyleSheet($xsl);\n\n    // Transform XML to HTML\n    echo $proc->transformToXML($xml);\n    exit;\n}\n?>