$result = mysql_query($query);\n// if successful query, return the records\nif ($result)\n{\n    // if not empty result, return array of records\n    if (mysql_num_rows($result))\n    {\n        $records = array();\n        while ($row = mysql_fetch_assoc($result))\n        {\n            $records[] = $row;\n        }\n        echo json_encode($records);\n        return;\n    }\n}