<?php\n// Database connection details\n$serverName = "your_server_name";\n$connectionOptions = array(\n    "Database" => "your_database_name",\n    "Uid" => "your_username",\n    "PWD" => "your_password"\n);\n\n// Establishes the connection\n$conn = sqlsrv_connect($serverName, $connectionOptions);\n\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Your insert query\n$insertSql = "INSERT INTO your_table_name (column1, column2) VALUES (?, ?)";\n$params = array('value1', 'value2');\n$stmt = sqlsrv_query($conn, $insertSql, $params);\n\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Get the last inserted ID\n$identitySql = "SELECT SCOPE_IDENTITY() AS ins_id";\n$stmtIdentity = sqlsrv_query($conn, $identitySql);\n\nif ($stmtIdentity === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$row = sqlsrv_fetch_array($stmtIdentity, SQLSRV_FETCH_ASSOC);\n$lastInsertId = $row['ins_id'];\n\necho "Last Inserted ID: " . $lastInsertId;\n\n// Close the connection\nsqlsrv_close($conn);\n?>