// Sending a friend request\n$stmt = $pdo->prepare("INSERT INTO friendships (user_id, friend_id, status) VALUES (:user_id, :friend_id, 'pending')");\n$stmt->execute(['user_id' => $userId, 'friend_id' => $friendId]);\n\n// Accepting a friend request\n$stmt = $pdo->prepare("UPDATE friendships SET status = 'accepted' WHERE user_id = :user_id AND friend_id = :friend_id");\n$stmt->execute(['user_id' => $friendId, 'friend_id' => $userId]);