function isBusinessHours(DateTime $dateTime) {\n    $dayOfWeek = $dateTime->format('N'); // 1 (for Monday) through 7 (for Sunday)\n    $hour = (int)$dateTime->format('H');\n\n    // Check if it's a weekday (Monday to Friday) and within business hours (9am to 5pm)\n    if ($dayOfWeek >= 1 && $dayOfWeek <= 5 && $hour >= 9 && $hour < 17) {\n        return true;\n    }\n    return false;\n}\n\nfunction nextBusinessDayStart(DateTime $dateTime) {\n    $dayOfWeek = $dateTime->format('N');\n\n    if ($dayOfWeek >= 1 && $dayOfWeek <= 5 && $dateTime->format('H') < 9) {\n        $dateTime->setTime(9, 0);\n    } else {\n        // Move to the next day\n        $dateTime->modify('+1 day')->setTime(9, 0);\n\n        // If it's a weekend, move to the next Monday\n        while ($dateTime->format('N') > 5) {\n            $dateTime->modify('+1 day');\n        }\n    }\n    return $dateTime;\n}\n\n// Testing the functions with different times\n$testDateTime = new DateTime('2023-10-10 07:30:00'); // Set your test date and time here\necho "Is business hours: " . (isBusinessHours($testDateTime) ? 'Yes' : 'No') . "\n";\necho "Next business day start: " . nextBusinessDayStart(clone $testDateTime)->format('Y-m-d H:i:s') . "\n";