// In your specific model file, e.g., facilities.php\npublic function describe($table) {\n    $sql = "SELECT column_name AS name, data_type AS type FROM all_tab_columns WHERE table_name = '{$table}'";\n    \n    if (!$this->execute($sql)) {\n        return false;\n    }\n    return $this->fetchAll($sql);\n}\n\n// Or create a custom datasource extending the DboSource\nclass CustomOracleSource extends DboOracle {\n    public function describe($model) {\n        $sql = "SELECT column_name AS name, data_type AS type FROM all_tab_columns WHERE table_name = '{$model->useTable}'";\n        \n        if (!$this->execute($sql)) {\n            return false;\n        }\n        return $this->fetchAll($sql);\n    }\n}