class Survey extends CI_Controller {\n\n    public function index() {\n        // Default action when no specific method is called\n        echo "no surveyID set";\n    }\n\n    public function take_survey($surveyID) {\n        // Logic for taking a survey using $surveyID\n        echo "Taking survey with ID: " . $surveyID;\n    }\n\n    public function survey_session($surveySessionID, $guestID) {\n        // Logic for taking a survey session with $surveySessionID and $guestID\n        echo "Survey session ID: " . $surveySessionID . " for guest ID: " . $guestID;\n    }\n}