<?php\n// Database connection\n$con = mysql_connect("localhost", "****", "****");\nif (!$con) {\n    die('Could not connect: ' . mysql_error());\n}\n\nmysql_select_db("******", $con);\n\n// Query to fetch articles ordered by timestamp descending\n$result = mysql_query("SELECT * FROM dbArticle ORDER BY timestamp DESC LIMIT 6");\n\n$first = true; // Flag to identify the first row\n\nwhile ($row = mysql_fetch_array($result)) {\n    if ($first) {\n        // Display the first row differently\n        echo "<h2 class=\"heading1\">". $row['title'] ."</h2>";\n        echo "By: ".$row['username']." Type: ".$row['type']." Posted: ".$row['timestamp']."<br/>";\n        $body = $row['body'];\n        echo "<p>".substr($body, 0, 260)."...<span class=\"tool\"><a class=\"blue\" href=\"index.php?pageContent=readArticle&id=".$row['id']."\">Read More</a></span></p><hr/>";\n        \n        $first = false; // Set the flag to false after the first row\n    } else {\n        // Display the rest of the rows\n        echo "<h3 class=\"heading2\">". $row['title'] ."</h3>";\n        echo "By: ".$row['username']." Posted: ".$row['timestamp']."<br/>";\n        echo "<p><a href=\"index.php?pageContent=readArticle&id=".$row['id']."\">Read More</a></p><hr/>";\n    }\n}\n\nmysql_close($con);\n?>