<?php\n$xmlFile = 'path/to/your/articles.xml';\n\nif (file_exists($xmlFile)) {\n    $xml = simplexml_load_file($xmlFile);\n    \n    if ($xml === false) {\n        echo 'Error: Failed to load XML file.';\n    } else {\n        foreach ($xml->article as $article) {\n            echo '<h2>' . htmlspecialchars($article->title) . '</h2>';\n            echo '<p>' . htmlspecialchars($article->content) . '</p>';\n            echo '<small>Published on: ' . htmlspecialchars($article->date) . '</small><br>';\n        }\n    }\n} else {\n    echo 'Error: XML file does not exist.';\n}\n?>