<?php\n// Function to get timezone by IP address\nfunction get_timezone_by_ip($ip) {\n    $url = "https://ipinfo.io/{$ip}/timezone";\n    $response = file_get_contents($url);\n    return $response ? trim($response) : 'UTC';\n}\n\n// Get the user's IP address (ensure you have a secure way to get the real IP)\n$user_ip = $_SERVER['REMOTE_ADDR'];\n\n// Get the timezone for the user's IP address\n$user_timezone = get_timezone_by_ip($user_ip);\n\n// Set the default timezone\ndate_default_timezone_set($user_timezone);\n\n// Verify the timezone setting\necho "Current Timezone: " . date_default_timezone_get();\necho "Current Date and Time: " . date('Y-m-d H:i:s');\n?>