function cleanString($var) {\n    // Case 1: If the string contains ' (as', remove ' (as' and everything after it\n    if (strpos($var, ' (as') !== false) {\n        $var = preg_replace('/ \(as.*$/', '', $var);\n    } \n    // Case 2: If the string does not contain ' (as', remove everything after the first word and trim whitespace\n    else {\n        $var = preg_split('/[\s(]/', $var)[0];\n    }\n\n    // Remove any remaining parentheses and trim whitespace\n    $var = trim(preg_replace('/[()]/', '', $var));\n\n    return $var;\n}\n\n// Test cases\n$examples = [\n    'Tom Greenleaf (as John Dunn Hill)',\n    '(screenplay) (as The Wibberleys) &',\n    '(novella "Four Past Midnight: Secret Window, Secret Garden")'\n];\n\nforeach ($examples as $example) {\n    echo cleanString($example) . PHP_EOL;\n}