<?php\n// Retrieve the data from the database\n$sql = "SELECT your_column FROM your_table WHERE id = :id";\n$stmt = $pdo->prepare($sql);\n$stmt->bindParam(':id', $id);\n$stmt->execute();\n$row = $stmt->fetch(PDO::FETCH_ASSOC);\n\n$retrieved_data = $row['your_column'];\n\n// Use htmlspecialchars to safely output data into the textarea\n$safe_output = htmlspecialchars($retrieved_data, ENT_QUOTES, 'UTF-8');\n?>\n<textarea><?php echo $safe_output; ?></textarea>