<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    // Check for errors in the upload process\n    if ($_FILES['uploaded_file']['error'] == UPLOAD_ERR_INI_SIZE) {\n        echo "The uploaded file exceeds the maximum size allowed by the server.";\n    } elseif ($_FILES['uploaded_file']['error'] != UPLOAD_ERR_OK) {\n        echo "An error occurred during the file upload.";\n    } else {\n        // Process the uploaded file\n        $fileTmpPath = $_FILES['uploaded_file']['tmp_name'];\n        $fileName = $_FILES['uploaded_file']['name'];\n        $fileSize = $_FILES['uploaded_file']['size'];\n        $fileType = $_FILES['uploaded_file']['type'];\n        \n        // Check file size manually if needed\n        $maxFileSize = 10 * 1024 * 1024; // 10 MB\n        if ($fileSize > $maxFileSize) {\n            echo "The uploaded file exceeds the maximum size allowed by the script.";\n        } else {\n            // Move the file to the desired directory\n            $uploadFileDir = './uploaded_files/';\n            $dest_path = $uploadFileDir . $fileName;\n\n            if (move_uploaded_file($fileTmpPath, $dest_path)) {\n                echo "File is successfully uploaded.";\n            } else {\n                echo "There was an error moving the uploaded file.";\n            }\n        }\n    }\n}\n?>