<?php\n// Calculate previous and next week dates\n$previousWeekStart = clone $startOfWeek;\n$previousWeekStart->modify('-1 week');\n\n$nextWeekStart = clone $startOfWeek;\n$nextWeekStart->modify('+1 week');\n\n$previousWeekStartStr = $previousWeekStart->format('Y-m-d');\n$nextWeekStartStr = $nextWeekStart->format('Y-m-d');\n\n// Display buttons with links to the previous and next week\necho '<a href="?start_date=' . $previousWeekStartStr . '">Previous Week</a>';\necho '<a href="?start_date=' . $nextWeekStartStr . '">Next Week</a>';\n?>