<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $word = isset($_POST['word']) ? trim($_POST['word']) : '';\n    $anotherVariable = isset($_POST['anotherVariable']) ? trim($_POST['anotherVariable']) : '';\n\n    // Validate and sanitize your input\n    $word = filter_var($word, FILTER_SANITIZE_STRING);\n    $anotherVariable = filter_var($anotherVariable, FILTER_SANITIZE_STRING);\n\n    // Database connection (use prepared statements to prevent SQL injection)\n    $conn = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    $stmt = $conn->prepare("UPDATE your_table SET your_column = ? WHERE another_column = ?");\n    $stmt->bind_param('ss', $word, $anotherVariable);\n    \n    if ($stmt->execute()) {\n        echo "Update successful";\n    } else {\n        echo "Error: " . $stmt->error;\n    }\n\n    $stmt->close();\n    $conn->close();\n}\n?>