<?php\nnamespace App\Controllers;\n\nuse App\Models\User;\n\nclass UserController\n{\n    public function index()\n    {\n        // Fetch all users\n        $users = User::all();\n        return view('users.index', ['users' => $users]);\n    }\n\n    public function show($id)\n    {\n        // Fetch a single user\n        $user = User::find($id);\n        return view('users.show', ['user' => $user]);\n    }\n}