<?php\nrequire 'vendor/autoload.php';\n\nuse Czim\Git\GitRepository;\n\n$repoPath = '/path/to/your/repo';\n$repo = new GitRepository($repoPath);\n\n// Get the latest commits\n$commits = $repo->log(['limit' => 5]);\n\nforeach ($commits as $commit) {\n    echo 'Committer: ' . $commit->getCommitterName() . PHP_EOL;\n    echo 'Date: ' . $commit->getDate() . PHP_EOL;\n    echo 'Message: ' . $commit->getMessage() . PHP_EOL;\n    echo '---' . PHP_EOL;\n}\n\n// Get branches\n$branches = $repo->getBranches();\n\nforeach ($branches as $branch) {\n    echo 'Branch: ' . $branch . PHP_EOL;\n}\n?>