<?php\n\ninclude("FeedWriter.php");\ninclude('simple_html_dom.php');\n\n// Fetch and parse the HTML content\n$html = file_get_html('http://www.website.com');\n\n// Create an instance of FeedWriter class\n$TestFeed = new FeedWriter(RSS2);\n\n// Use wrapper functions for common channel elements\n$TestFeed->setTitle('Testing & Checking the RSS writer class');\n$TestFeed->setLink('http://www.ajaxray.com/projects/rss');\n$TestFeed->setDescription('This is test of creating a RSS 2.0 feed Universal Feed Writer');\n\n// Image title and link must match with the 'title' and 'link' channel elements for valid RSS 2.0\n$TestFeed->setImage('Testing the RSS writer class', 'http://www.ajaxray.com/projects/rss', 'http://www.rightbrainsolution.com/images/logo.gif');\n\n// Loop through each article and create feed items\nforeach ($html->find('td[width="380"] p table') as $article) {\n    // Create an empty FeedItem\n    $newItem = $TestFeed->createNewItem();\n\n    // Add elements to the feed item\n    $newItem->setTitle(html_entity_decode($article->find('span.title', 0)->innertext, ENT_NOQUOTES, 'UTF-8'));\n    $newItem->setLink($article->find('.lesMer', 0)->href);\n    $newItem->setDate(strtotime($article->find('span.presseDato', 0)->plaintext));\n    $newItem->setDescription("<img src='" . $article->find('img[width="100"]', 0)->src . "'><p>" . $article->find('.ingress', 0)->innertext . "</p>");\n\n    // Now add the feed item\n    $TestFeed->addItem($newItem);\n}\n\n// Everything is done. Now generate the feed.\n$TestFeed->genarateFeed();\n\n?>