<?php\n  $items = array(12, 17, 27, 26, 5); // an array of IDs, in proper order\n  \n  // Start building the SQL query\n  $sql = "UPDATE `table` SET `priorityLevel` = CASE `ID`";\n  \n  // Add each item and its corresponding priority level\n  foreach ($items as $order => $item) {\n    $sql .= " WHEN {$item} THEN {$order}";\n  }\n  \n  // Close the CASE statement and add the WHERE clause\n  $sql .= " END WHERE `ID` IN (" . implode(", ", $items) . ")";\n  \n  // Execute the query\n  $database->query($sql);\n?>