<?php\n\nfunction getGeolocation($ip, $access_key) {\n    // API URL\n    $url = "http://api.ipstack.com/{$ip}?access_key={$access_key}";\n    \n    // Initialize a cURL session\n    $ch = curl_init($url);\n    \n    // Set cURL options\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Execute cURL session and get the response\n    $response = curl_exec($ch);\n    \n    // Close cURL session\n    curl_close($ch);\n    \n    // Decode JSON response\n    $data = json_decode($response, true);\n    \n    // Check if the API call was successful\n    if (isset($data['latitude']) && isset($data['longitude'])) {\n        return [\n            'latitude' => $data['latitude'],\n            'longitude' => $data['longitude']\n        ];\n    } else {\n        // Handle error\n        return null;\n    }\n}\n\n// Example usage\n$ip = '134.201.250.155';\n$access_key = 'your_ipstack_access_key_here';\n\n$location = getGeolocation($ip, $access_key);\n\nif ($location) {\n    echo "Latitude: " . $location['latitude'] . "\n";\n    echo "Longitude: " . $location['longitude'] . "\n";\n} else {\n    echo "Unable to retrieve geolocation data.\n";\n}\n\n?>