// Use mysqli or PDO instead of the deprecated mysql\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Secure SQL query to list unique threads sorted by the most recent post\n$sql = "SELECT DISTINCT thread_id, MAX(post_date) AS latest_post_date \n        FROM posts \n        GROUP BY thread_id \n        ORDER BY latest_post_date DESC \n        LIMIT 65";\n\n$result = $mysqli->query($sql);\n\n// Check if the query was successful\nif ($result) {\n    echo "<ul>";\n    while ($row = $result->fetch_assoc()) {\n        echo "<li>" . htmlspecialchars($row['thread_id']) . "</li>";\n    }\n    echo "</ul>";\n} else {\n    echo "Error: " . $mysqli->error;\n}\n\n// Close the connection\n$mysqli->close();