<?php\n// Sample data\n$result = [\n    ['line1' => 'Address 1', 'line2' => 'City 1', 'line3' => 'Country 1'],\n    ['line1' => 'Address 2', 'line2' => 'City 2', 'line3' => 'Country 2']\n];\n\n// Create an array to hold the formatted addresses\n$formattedAddresses = [];\n\nforeach ($result as $addr) {\n    $address = [];\n    foreach ($addr as $line) {\n        $address[] = $line;\n    }\n    $formattedAddresses[] = $address;\n}\n\n// Set the content type to application/json\nheader('Content-Type: application/json');\n\n// Encode the array to JSON and output it\necho json_encode($formattedAddresses);\n?>