// Get the raw POST data\n$postData = file_get_contents('php://input');\n\n// Parse the raw POST data\nparse_str($postData, $parsedData);\n\n// Initialize an array to hold the checkbox values\n$checkboxValues = [];\n\n// Check if 'option' is present and handle it accordingly\nif (isset($parsedData['option'])) {\n    if (is_array($parsedData['option'])) {\n        // If 'option' is an array, merge it with the checkbox values array\n        $checkboxValues = array_merge($checkboxValues, $parsedData['option']);\n    } else {\n        // If 'option' is a single value, add it to the checkbox values array\n        $checkboxValues[] = $parsedData['option'];\n    }\n}\n\n// Output the checkbox values\nprint_r($checkboxValues);