<?php\nfunction getMacAddress() {\n    ob_start();\n    system('getmac');\n    $content = ob_get_contents();\n    ob_end_clean();\n    $macAddress = substr($content, 0, 17);\n    return $macAddress;\n}\n\n$allowedMacAddress = '00:1A:2B:3C:4D:5E'; // Replace with authorized MAC address\n\nif (getMacAddress() !== $allowedMacAddress) {\n    die('Unauthorized access.');\n}\n\n// Your protected PHP code here\n?>