<?php\ndefined('BASEPATH') OR exit('No direct script access allowed');\n\nclass Hotel extends CI_Controller {\n\n    public function roomAvailability($hotel_id, $room_id) {\n        // Your logic here\n        // For example:\n        \n        // Load the model\n        $this->load->model('hotel_model');\n        \n        // Get room availability from the model\n        $availability = $this->hotel_model->get_room_availability($hotel_id, $room_id);\n        \n        // Load a view and pass the availability data\n        $data['availability'] = $availability;\n        $this->load->view('room_availability', $data);\n    }\n}\n?>