// Database connection (replace with your actual database connection details)\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare and bind\n$stmt = $mysqli->prepare("INSERT IGNORE INTO locations (name, address, city, state, zip) VALUES (?, ?, ?, ?, ?)");\n$stmt->bind_param("sssss", $venueName, $streetAddress, $city, $state, $zip);\n\n// Set parameters and execute\n$venueName = 'Soboba Casino';\n$streetAddress = '23333 Soboba Road';\n$city = 'San Jacinto';\n$state = 'CA';\n$zip = '92581';\n\n$stmt->execute();\n\n// Check for errors\nif ($stmt->error) {\n    die("Execute failed: " . $stmt->error);\n}\n\necho "New record inserted successfully";\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();