// Example of storing HTML in a static file\n\n   $group_id = 1; // Example group ID\n   $file_path = "/path/to/cache/group_$group_id.html";\n   $html_content = '<table>...</table>'; // Generated HTML content\n\n   // Storing the HTML content in a file\n   file_put_contents($file_path, $html_content);\n\n   // Retrieving the HTML content from the file\n   if (file_exists($file_path)) {\n       $html_content = file_get_contents($file_path);\n   }