<?php\n// login.php\nsession_start();\n\n// Sample authentication (replace with actual authentication logic)\n$username = $_POST['username'];\n$password = $_POST['password'];\n\nif (authenticate($username, $password)) {\n    // Set secure, HttpOnly cookie\n    setcookie(\n        "session_id",\n        session_id(),\n        [\n            'expires' => time() + 3600, // 1 hour\n            'path' => '/',\n            'domain' => '', // Set your domain\n            'secure' => true, // Only send cookie over HTTPS\n            'httponly' => true, // Accessible only via HTTP(S), not JavaScript\n            'samesite' => 'Strict', // Adjust based on your needs (Strict, Lax, None)\n        ]\n    );\n    echo json_encode(['status' => 'success']);\n} else {\n    echo json_encode(['status' => 'error', 'message' => 'Invalid credentials']);\n}\n\nfunction authenticate($username, $password) {\n    // Replace with actual authentication logic\n    // Example: return $username === 'user' && $password === 'pass';\n    return true;\n}\n?>