// Assuming you have a valid Zend_Db_Adapter_Mysqli $db instance\n\n// Create a custom method to run unbuffered queries\nfunction runUnbufferedQuery($db, $sql)\n{\n    // Get the mysqli resource from the Zend_Db_Adapter_Mysqli instance\n    $mysqli = $db->getConnection();\n    \n    // Disable autocommit mode\n    $mysqli->autocommit(FALSE);\n\n    // Execute the unbuffered query\n    $result = $mysqli->query($sql, MYSQLI_USE_RESULT);\n\n    if ($result === false) {\n        throw new Exception('Query failed: ' . $mysqli->error);\n    }\n\n    // Fetch results one by one\n    while ($row = $result->fetch_assoc()) {\n        // Process each row\n        print_r($row);\n    }\n\n    // Free the result set\n    $result->free();\n\n    // Commit the transaction\n    $mysqli->commit();\n\n    // Re-enable autocommit mode\n    $mysqli->autocommit(TRUE);\n}\n\n// Your SQL query\n$sql = "SELECT * FROM your_large_table";\n\n// Run the unbuffered query\nrunUnbufferedQuery($db, $sql);