<?php\n$login_url = 'http://example.com/wp-login.php';\n$redirect_url = 'http://example.com/wp-admin/';\n$username = 'admin';\n$password = 'password';\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL to login page\ncurl_setopt($ch, CURLOPT_URL, $login_url);\ncurl_setopt($ch, CURLOPT_POST, 1);\n\n// Set the post fields\n$post_fields = http_build_query([\n    'log' => $username,\n    'pwd' => $password,\n    'wp-submit' => 'Log In',\n    'redirect_to' => $redirect_url,\n    'testcookie' => '1'\n]);\n\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $post_fields);\n\n// Set options to maintain cookies\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookie.txt');\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookie.txt');\n\n// Follow any redirects\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);\n\n// Set return transfer to true to store the response in a variable\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the request\n$response = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    // Optionally, you can print the response for debugging\n    // echo $response;\n}\n\n// Close the cURL session\ncurl_close($ch);\n\n// Redirect to the admin page\nheader("Location: $redirect_url");\nexit;\n?>