<?php\nif (isset($_GET['fileURL'])) {\n    $fileURL = $_GET['fileURL'];\n\n    // Validate the URL to prevent security issues\n    if (filter_var($fileURL, FILTER_VALIDATE_URL) === FALSE) {\n        die('Invalid URL');\n    }\n\n    // Open a stream from the remote file\n    $handle = fopen($fileURL, 'rb');\n    if ($handle === FALSE) {\n        die('Could not open file stream');\n    }\n\n    // Get the file name from the URL\n    $filename = basename($fileURL);\n\n    // Send the headers to force download\n    header("Pragma: public"); // required\n    header("Expires: 0");\n    header("Cache-Control: must-revalidate, post-check=0, pre-check=0");\n    header("Cache-Control: private", false);\n    header("Content-Type: application/octet-stream");\n    header("Content-Disposition: attachment; filename=\"$filename\";");\n    header("Content-Transfer-Encoding: binary");\n\n    // Send the file content to the user\n    while (!feof($handle)) {\n        echo fread($handle, 8192);\n        ob_flush();\n        flush();\n    }\n\n    // Close the file handle\n    fclose($handle);\n    exit();\n} else {\n    die('No file URL provided');\n}\n?>