<?php\nfunction smoothArray($array, $newSize) {\n    $oldSize = count($array);\n    if ($newSize >= $oldSize) {\n        return $array;\n    }\n\n    $step = $oldSize / $newSize;\n    $result = [];\n\n    for ($i = 0; $i < $newSize; $i++) {\n        $start = floor($i * $step);\n        $end = floor(($i + 1) * $step);\n        $sum = 0;\n\n        for ($j = $start; $j < $end; $j++) {\n            $sum += $array[$j];\n        }\n\n        // If the last segment is not a full segment, adjust the sum\n        if ($i == $newSize - 1 && $end < $oldSize) {\n            for ($j = $end; $j < $oldSize; $j++) {\n                $sum += $array[$j];\n            }\n        }\n\n        $result[] = $sum;\n    }\n\n    return $result;\n}\n\n$array1 = [25, 50, 25];\n$array2 = [10, 15, 20, 25];\n$array3 = [10, 10, 10, 10, 11];\n\nprint_r(smoothArray($array1, 2));\nprint_r(smoothArray($array2, 2));\nprint_r(smoothArray($array3, 2));\n?>