<?php\n\nclass MyForm extends Zend_Form\n{\n    public function init()\n    {\n        // Create the text element\n        $name = new Zend_Form_Element_Text('name');\n        $name->setLabel('Name')\n             ->setDecorators(array(\n                 'ViewHelper',\n                 'Errors',\n                 array(array('data' => 'HtmlTag'), array(\n                     'tag' => 'dd', \n                     'closeOnly' => true, \n                     'placement' => 'prepend'\n                 )),\n                 array(array('openTag' => 'HtmlTag'), array(\n                     'tag' => 'dd', \n                     'openOnly' => true\n                 )),\n                 'Label',\n                 array(array('row' => 'HtmlTag'), array('tag' => 'dt'))\n             ));\n\n        // Create the link element using a custom decorator\n        $link = new Zend_Form_Element_Note('link');\n        $link->setValue('<a href="#">My Link</a>')\n             ->setDecorators(array(\n                 array('ViewHelper', array('escape' => false)),\n                 array(array('data' => 'HtmlTag'), array(\n                     'tag' => 'dd', \n                     'closeOnly' => true, \n                     'placement' => 'prepend'\n                 )),\n                 array(array('openTag' => 'HtmlTag'), array(\n                     'tag' => 'dd', \n                     'openOnly' => true\n                 )),\n             ));\n\n        // Add elements to form\n        $this->addElements(array($name, $link));\n\n        // Add display group\n        $this->addDisplayGroup(array('name', 'link'), 'people', array(\n            'decorators' => array(\n                'FormElements',\n                'Fieldset',\n            )\n        ));\n    }\n}