<?php\n// URL of the Google Calendar iframe\n$calendarUrl = 'https://calendar.google.com/calendar/embed?src=your_calendar_id&ctz=your_timezone';\n\n// Fetch the content of the Google Calendar iframe\n$calendarHtml = file_get_contents($calendarUrl);\n\n// Check if the fetching was successful\nif ($calendarHtml === FALSE) {\n    die('Error fetching calendar');\n}\n\n// Modify the links in the fetched HTML\n$modifiedHtml = preg_replace_callback(\n    '/<a href="([^"]+)"/i',\n    function ($matches) {\n        // Extract the URL\n        $url = $matches[1];\n\n        // Check if it's a redirect URL\n        if (strpos($url, 'https://www.google.com/url?q=') === 0) {\n            // Decode the URL\n            $url = urldecode(parse_url($url, PHP_URL_QUERY));\n\n            // Remove the "q=" parameter\n            parse_str($url, $queryParams);\n            $url = $queryParams['q'];\n        }\n\n        // Return the modified link\n        return '<a href="' . htmlspecialchars($url) . '"';\n    },\n    $calendarHtml\n);\n\n// Output the modified HTML\necho $modifiedHtml;\n?>