<?php\nclass Actividad extends AppModel {\n    var $name = 'Actividad';\n    var $validate = array(\n        'maxfield' => array(\n            'rule' => array('chkValue'),\n            'message' => 'maxfield must be greater than or equal to minfield'\n        )\n    );\n\n    function chkValue($data) {\n        // Access both fields from the current data set\n        $minFieldValue = isset($this->data[$this->alias]['minfield']) ? $this->data[$this->alias]['minfield'] : null;\n        $maxFieldValue = isset($data['maxfield']) ? $data['maxfield'] : null;\n\n        // Perform the comparison if both values are set\n        if ($minFieldValue !== null && $maxFieldValue !== null) {\n            return $maxFieldValue >= $minFieldValue;\n        }\n        \n        // Return false if either field is missing\n        return false;\n    }\n}