// Example of storing HTML in MySQL\n   // Assuming you have a table `cached_results` with columns `group_id` and `html_content`\n\n   // Storing the HTML content\n   $group_id = 1; // Example group ID\n   $html_content = '<table>...</table>'; // Generated HTML content\n\n   $stmt = $pdo->prepare("REPLACE INTO cached_results (group_id, html_content) VALUES (:group_id, :html_content)");\n   $stmt->execute([':group_id' => $group_id, ':html_content' => $html_content]);\n\n   // Retrieving the HTML content\n   $stmt = $pdo->prepare("SELECT html_content FROM cached_results WHERE group_id = :group_id");\n   $stmt->execute([':group_id' => $group_id]);\n   $html_content = $stmt->fetchColumn();