<?php\ninclude('adodb5/adodb.inc.php');  // Include the ADOdb library\n\n// Connect to the database\n$db = NewADOConnection('mysqli');  // Use the appropriate driver\n$db->Connect('hostname', 'username', 'password', 'database');\n\n// Enable caching\n$cacheSeconds = 3600;  // Cache duration in seconds (e.g., 1 hour)\n\n// Execute a cached query\n$sql = "SELECT * FROM my_table";\n$result = $db->CacheExecute($cacheSeconds, $sql);\n\n// Fetch the results\nwhile ($row = $result->FetchRow()) {\n    print_r($row);\n}\n\n// Close the connection\n$db->Close();\n?>