function generate_guid() {\n    if (function_exists('com_create_guid') === true) {\n        return trim(com_create_guid(), '{}');\n    }\n\n    return sprintf(\n        '%04X%04X-%04X-%04X-%04X-%04X%04X%04X',\n        mt_rand(0, 0xffff),\n        mt_rand(0, 0xffff),\n        mt_rand(0, 0xffff),\n        mt_rand(0, 0x0fff) | 0x4000,\n        mt_rand(0, 0x3fff) | 0x8000,\n        mt_rand(0, 0xffff),\n        mt_rand(0, 0xffff),\n        mt_rand(0, 0xffff)\n    );\n}\n\n// Usage\n$guid = generate_guid();\necho $guid;