<?php\n// Set the session cookie parameters\nsession_set_cookie_params([\n    'lifetime' => 0, // Session cookie will be deleted when the browser is closed\n    'path' => '/', // Available in all paths in the domain\n    'domain' => '.example.com', // Available to all subdomains of example.com\n    'secure' => true, // Cookie will only be sent over secure connections\n    'httponly' => true, // Cookie can only be accessed through the HTTP protocol\n    'samesite' => 'Lax' // CSRF protection\n]);\n\n// Start the session\nsession_start();\n\n// Store session data\n$_SESSION["SessionID"] = "your_session_data";\n?>