function splitSqlFile($sql) {\n    $queries = [];\n    $delimiter = ';';\n    $sql = explode($delimiter, $sql);\n    foreach ($sql as $query) {\n        // Trim whitespace and remove comments\n        $query = trim(preg_replace('/--.*?\n|\/\*.*?\*\/|#.*?\n/', '', $query));\n        if (!empty($query)) {\n            $queries[] = $query . $delimiter;\n        }\n    }\n    return $queries;\n}\n\n$content = file_get_contents('path_to_your_sql_dump_file.sql');\n$queries = splitSqlFile($content);\n\nforeach ($queries as $query) {\n    // Execute each query using your MySQL class\n    $mysql->execute($query);\n}