<?php\n   // Default language\n   $lang = 'en';\n\n   // Check if lang parameter is set in the URL\n   if (isset($_GET['lang']) && in_array($_GET['lang'], ['en', 'es'])) {\n       $lang = $_GET['lang'];\n   }\n\n   // Load the language file\n   $translations = include "languages/{$lang}.php";\n\n   // Usage\n   echo $translations['first_name']; // Outputs 'First Name' or 'Nombre' based on selected language\n   echo $translations['operation_not_allowed']; // Outputs 'This operation is not allowed!' or '¡Esta operación no está permitida!'\n   ?>