<?php\n   require 'vendor/autoload.php'; // Include Guzzle if you installed it via Composer\n\n   use GuzzleHttp\Client;\n\n   function getVisitorCountry() {\n       $client = new Client();\n       $response = $client->request('GET', 'https://ipinfo.io/json');\n       $data = json_decode($response->getBody(), true);\n\n       return $data['country'] ?? null;\n   }\n\n   $country = getVisitorCountry();\n\n   if ($country === 'FI') {\n       echo '<div class="additional-content">This is additional content for visitors from Finland.</div>';\n   }\n   ?>\n\n   <style>\n       .additional-content {\n           position: fixed;\n           left: 0;\n           top: 50%;\n           background-color: #f0f0f0;\n           padding: 10px;\n           border: 1px solid #ccc;\n       }\n   </style>