<?php\ntry {\n    // Create a new COM object for ADO\n    $conn = new COM("ADODB.Connection");\n\n    // Connection string for OLEDB\n    $connectionString = "Provider=SQLOLEDB;Data Source=your_server;Initial Catalog=your_database;User ID=your_username;Password=your_password;";\n\n    // Open the connection\n    $conn->Open($connectionString);\n\n    // Execute a query\n    $recordset = new COM("ADODB.Recordset");\n    $query = "SELECT * FROM your_table";\n    $recordset->Open($query, $conn);\n\n    // Fetch the results\n    while (!$recordset->EOF) {\n        echo $recordset->Fields["your_column"]->Value . "<br>";\n        $recordset->MoveNext();\n    }\n\n    // Close the recordset and connection\n    $recordset->Close();\n    $conn->Close();\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>