<?php\n\n// Define the path as relative\n$path = "./";\n\nfunction listagain($pth)\n{\n    // Using the opendir function\n    $dir_handle = @opendir($pth) or die("Unable to open $pth");\n\n    echo "Directory Listing of $pth<br/>";\n\n    // Running the while loop\n    while ($file = readdir($dir_handle)) \n    {\n        // Skip the current (.) and parent (..) directories\n        if ($file == "." || $file == "..") {\n            continue;\n        }\n\n        // Create the full path for the current file or directory\n        $fullPath = $pth . "/" . $file;\n\n        // Check whether the current file is a directory\n        if (is_dir($fullPath))\n        {\n            // If it is, generate its list of files\n            listagain($fullPath);\n        }\n        else\n        {\n            echo "<a href='$fullPath'>$file</a><br/>";\n        }\n    }\n    // Closing the directory\n    closedir($dir_handle);\n}\n\nlistagain($path);\n\n?>