<?php\n// Load the public key\n$publicKey = file_get_contents('public_key.pem');\n\n// The data and signature (example - ensure you use actual data and signature)\n$data = "This is the data to be signed";\n$encodedSignature = "Base64EncodedSignatureHere"; // replace with actual base64 encoded signature\n\n// Decode the signature from base64\n$signature = base64_decode($encodedSignature);\n\n// Verify the signature\n$publicKeyId = openssl_get_publickey($publicKey);\n$verified = openssl_verify($data, $signature, $publicKeyId, OPENSSL_ALGO_SHA256);\n\n// Free the key resource\nopenssl_free_key($publicKeyId);\n\nif ($verified == 1) {\n    echo "Signature is valid.";\n} elseif ($verified == 0) {\n    echo "Signature is invalid.";\n} else {\n    echo "Error verifying signature.";\n}\n?>