<?php\n$originalArray = [\n    2 => (object) ['title' => 'Section', 'linked' => 1],\n    1 => (object) ['title' => 'Sub-Section', 'linked' => 1],\n    0 => (object) ['title' => 'Sub-Sub-Section', 'linked' => '']\n];\n\n// Reset the array to zero-based index\n$valuesArray = array_values($originalArray);\n\n// Reindex the array starting from 1\n$reindexedArray = [];\nforeach ($valuesArray as $key => $value) {\n    $reindexedArray[$key + 1] = $value;\n}\n\n// Print the reindexed array\nprint_r($reindexedArray);\n?>