$lol = "hello";\n$that = "* music\n* books\n* games\n* asos\n* cry";\n\n$lol = str_replace("*", $lol, $that);\n\n// Replace UNIX newlines (\n) with Windows newlines (\r\n)\n$lol = str_replace("\n", "\r\n", $lol);\n\n$name = str_replace(" ", "-", $lol);\n$myFile = "output/$name.csv";\n$fh = fopen($myFile, 'w') or die("can't open file");\n\n$stringData = $lol;\nfwrite($fh, $stringData);\nfclose($fh);